package src.basics_05;

public class Lab096 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// While -> Init -> Condition -> Body -> Increment

        // Do While
        // Do While -> Init -> Body -> Condition -> Increment
        int a = 0;
        do {
            System.out.println(a);
            a--;
        } while (a > -5);

	}

}
